mapper.select:

	SELECT userNo
	FROM users
	WHERE userId = #{userId}

mapper.update:

	UPDATE users
	SET userId = #{userId},
		userName = #{userName},
		userRepImg = #{userRepImg}
	WHERE userNo = #{userNo}

mapper.delete:

	DELETE FROM userrelation
	WHERE relationTo = #{relationTo}
	AND relationFrom = #{relationFrom}
